// Use the existing output field from previous node
const rawOutput = $json.output ?? "";

// Clean markdown and whitespace for ElevenLabs speech
const cleanedOutput = rawOutput
  .replace(/[*_~`>#-]/g, "")     // remove markdown characters
  .replace(/\s+/g, " ")          // normalize whitespace
  .trim();

return [
  {
    json: {
      output: cleanedOutput
    }
  }
];

// Escape MarkdownV2 reserved characters (escape backslash first)
function escapeMDV2(s) {
  return s
    .replace(/\\/g, '\\\\')
    .replace(/([_*\[\]()~`>#+\-=|{}.!])/g, '\\$1'); // asterisk (*) added back
}

// Split by UTF-8 bytes with natural breakpoints
function splitIntoUtf8ByteChunks(text, maxBytes = 3800) {
  const enc = new TextEncoder();
  const chunks = [];
  let start = 0;

  while (start < text.length) {
    let end = start, bytes = 0, lastBreak = -1;

    while (end < text.length) {
      const cp = text.codePointAt(end);
      const ch = String.fromCodePoint(cp);
      const b = enc.encode(ch).length;

      if (bytes + b > maxBytes) break;

      bytes += b;

      // Mark natural breakpoints
      if (ch === ' ' || ch === '\n' || ch === '.' || ch === '!' || ch === '?' || ch === ',') {
        lastBreak = end + (cp > 0xFFFF ? 2 : 1);
      }

      end += (cp > 0xFFFF ? 2 : 1);
    }

    // Use natural breakpoint if available and chunk is reasonably long
    if (end < text.length && lastBreak > start && (end - start) > 200) {
      end = lastBreak;
    }

    // Ensure we make progress
    if (end === start) {
      end = start + 1;
    }

    chunks.push(text.slice(start, end));
    start = end;
  }

  return chunks;
}

// ---- MAIN ----
const raw = String($json.cleanedResponse ?? $json.output ?? '').replace(/\r\n/g, '\n');
const escaped = escapeMDV2(raw);
const chunks = splitIntoUtf8ByteChunks(escaped, 3800);

return chunks.map((chunk, i) => ({
  json: {
    cleanedResponse: chunk,
    chunkIndex: i + 1,
    totalChunks: chunks.length,
    isMultipart: chunks.length > 1,
  }
}));
